import './App.css';
import { useState } from 'react';

function App() {

  const [post, setpost] = useState([{ title: 'ÎÇ®Ïûê ÏΩîÌä∏ Ï∂îÏ≤ú', heart: 0, pubTime: 'Wed Nov 01 2023 10:54:33 GMT+0900 (ÌïúÍµ≠ ÌëúÏ§ÄÏãú)' }
    , { title: 'Í∞ïÎÇ® Ïö∞Îèô ÎßõÏßë', heart: 0, pubTime: 'Wed Nov 01 2023 10:55:33 GMT+0900 (ÌïúÍµ≠ ÌëúÏ§ÄÏãú)' }
    , { title: 'ÌååÏù¥Ïç¨ ÎèÖÌïô', heart: 0, pubTime: 'Wed Nov 01 2023 10:56:33 GMT+0900 (ÌïúÍµ≠ ÌëúÏ§ÄÏãú)' }])

  const [modal, setmodal] = useState(false)
  const [thismodal, setthismodal] = useState(0)
  const [userInput, setuserInput] = useState('')
  return (
    <div className="App">

      <div className="black-nav">
        <div>Í∞úÎ∞ú blog </div>
        <button onClick={function () {
          let copy = [...post];
          console.log(copy)
          copy[0].title = 'Ïó¨Ïûê ÏΩîÌä∏ Ï∂îÏ≤ú';
          setpost(copy)
            ;
        }}>Ïó¨Ïûê ÏΩîÌä∏ Ï∂îÏ≤ú</button>

        <button onClick={function () {
          let titleResult
          titleResult = [...post].sort(function (a, b) {
            let x = a.title.toLowerCase();
            let y = b.title.toLowerCase();
            if (x < y) {
              return -1;
            }
            if (x > y) {
              return 1;
            }
            return 0;
          })
          setpost(titleResult)
        }}>Ïò§Î¶ÑÏ∞®ÏàúÏ†ïÎ†¨</button>

        <button onClick={function () {
          let titleResult
          titleResult = [...post].sort(function (a, b) {
            let x = a.title.toLowerCase();
            let y = b.title.toLowerCase();
            if (x < y) {
              return 1;
            }
            if (x > y) {
              return -1;
            }
            return 0;
          })
          setpost(titleResult)
        }}>ÎÇ¥Î¶ºÏ∞®ÏàúÏ†ïÎ†¨</button>

      </div>

      {
        post.map(function (a, i) {
          return (
            <div className="list" key={i}>
              <h4 onClick={function () {
                setmodal(!modal)
                setthismodal(i)
              }}> {post[i].title} <span onClick={function (e) {
                let copy = [...post];
                copy[i].heart += + 1;
                setpost(copy)
                e.stopPropagation()
              }} style={{ cursor: "pointer" }}>üëç</span>{post[i].heart}</h4>
              <p>{post[i].pubTime}</p>
            </div>
          )
        })
      }

      {
        modal === true ? <Modal post={post[thismodal]}></Modal> : null
      }

      <input type="text" onChange={function (e) {
        setuserInput(e.target.value)
      }} /><button onClick={function () {
        let copy = [...post]
        copy.push({ title: userInput, heart: 0, pubTime: new Date().toString() })
        setpost(copy)
      }}>Í≤åÏãú</button>

    </div>
  )
}

function Modal(props) {
  return (
    <div className="modal">
      <h4>{props.post.title}</h4>
      <p>{props.post.pubTime}</p>
    </div>
  )
}

export default App;
